import React, { Component } from 'react';

import { Link } from "react-router-dom";


class Recipe extends Component {
  state = {
    details: []
  }
  // componentDidMount = async () => {
  //   const title = this.props.location.state.recipe;
  //   const fetchData = await fetch(`https://cors-anywhere.herokuapp.com/http://food2fork.com/api/search?key=872498ad70c0abbf0571fc7ff3e7d991&q=${title}`);

  //   const localData = await fetchData.json();
  //   this.setState({ details: localData.recipes[0] });
  // }


  render() {
    const recipe = this.state.details;
    return (
      <div key={recipe.title} className="container">
        { this.state.details.length !== 0 &&
          <div>
            <img src={recipe.image_url} alt={recipe.title}/>
            <h3>{ recipe.title }</h3>
            <h4>
              Publisher: <span>{ recipe.publisher }</span>
            </h4>
            <p>Website: 
              <span><a href={recipe.publisher_url}>{recipe.publisher_url}</a></span>
            </p>
            <button>
              <Link to="/">Go Home</Link>
            </button>
          </div>
        }
      </div>
    );
  }
};

export default Recipe;
