import React, { Component } from 'react';

import { Link } from "react-router-dom";


class Recipe extends Component {
  constructor(props){
    super(props)
  this.state = {

    details: []
  }

  }  


  componentDidMount(){
    const title = this.props.location.state.recipe;
    let endpoint= `https://cors-anywhere.herokuapp.com/http://food2fork.com/api/search?key=54ebc42713b42b560c05d50131e7b7d4&q=${title}`
    fetch(endpoint)
      .then(response => response.json())
      .then(json => {
        this.setState({
          details: json.recipes[0]
        })
      })
    }




  render() {
    const recipe = this.state.details 
    return (
      <div className='new_page' key={recipe.title} >

          <div className='items'>
            <img className='toimage' src={recipe.image_url} alt={recipe.title}/>
            <h2>{ recipe.title }</h2>
            <p>Link to recipe: 
              <span><a href={recipe.publisher_url}>{recipe.publisher_url}</a></span>
            </p>
            <button className='home_button'>
              <Link to="/">Home</Link>
            </button>
          </div>
      </div>
    );
  }
};

export default Recipe;
