import React, { Component } from 'react';

import { Link } from "react-router-dom";


class Recipe extends Component {
  constructor(props){
    super(props)
  this.state = {
    details: []
  }
  this.getDetails = this.getDetails.bind(this)
  }  

  componentDidMount(){
    this.getDetails()
  }

  getDetails() {
    const title = this.props.location.state.recipe;
    let endpoint= `https://cors-anywhere.herokuapp.com/http://food2fork.com/api/search?key=0f601abe3f1498ec7886f8be22d95a23&q=${title}`
    fetch(endpoint)
      .then(response => response.json())
      .then(json => {
        this.setState({
          details: json.recipes[0]
        })
      })
}




  render() {
    const recipe = this.state.details;
    return (
      <div key={recipe.title} className="container">
        { this.state.details.length !== 0 &&
          <div>
            <img src={recipe.image_url} alt={recipe.title}/>
            <h3>{ recipe.title }</h3>
            <h4>
              Publisher: <span>{ recipe.publisher }</span>
            </h4>
            <p>Website: 
              <span><a href={recipe.publisher_url}>{recipe.publisher_url}</a></span>
            </p>
            <button>
              <Link to="/">Home</Link>
            </button>
          </div>
        }
      </div>
    );
  }
};

export default Recipe;
