import React, { Component } from 'react';

import { Link } from "react-router-dom";

const title = this.props.location.state.recipe


class Recipe extends Component {
  constructor(props) {
    super(props)
    this.state = {
      details: []
    }

    this.viewRecipe = this.viewRecipe.bind(this)
  }  

  componentDidMount(){
    this.viewRecipe()
  }

  viewRecipe() {
    let endPoint = 'https://cors-anywhere.herokuapp.com/http://food2fork.com/api/search?key=54ebc42713b42b560c05d50131e7b7d4&q=${title}'
    fetch(endPoint)
      .then(response => response.json())
      .then(json => {
        this.setState({
            details: json.recipes[0]
        })
      })
}

  render() {
    const recipe = this.state.details;
    return (
      <div key={recipe.title}>
        { this.state.details.length !== 0 &&
          <div>
            <img src={recipe.image_url} alt={recipe.title}/>
            <h3>{ recipe.title }</h3>
            <h4>
              Publisher: <span>{ recipe.publisher }</span>
            </h4>
            <p>Website: 
              <span><a href={recipe.publisher_url}>{recipe.publisher_url}</a></span>
            </p>
            <button className="active-recipe__button">
              <Link to="/">Go Home</Link>
            </button>
          </div>
        }
      </div>
    );
  }
};

export default Recipe;
