import React from 'react';

import { Link } from "react-router-dom";


class Recipe extends React.Component {
  state = {
    showRecipe: []
  }
  componentDidMount = async () => {
    const title = this.props.location.state.recipe;
    const req = await fetch(`http://food2fork.com/api/search?key=872498ad70c0abbf0571fc7ff3e7d991=${title}`);

    const res = await req.json();
    this.setState({ showRecipe: res.recipes[0] });
    // console.log(this.state.showRecipe);
  }
  render() {
    const recipe = this.state.showRecipe;
    return (
      <div key={recipe.title}>
        { this.state.showRecipe.length !== 0 &&
          <div>
            <img src={recipe.image_url} alt={recipe.title}/>
            <h3>{ recipe.title }</h3>
            <h4>
              Publisher: <span>{ recipe.publisher }</span>
            </h4>
            <p>Website: 
              <span><a href={recipe.publisher_url}>{recipe.publisher_url}</a></span>
            </p>
            <button className="active-recipe__button">
              <Link to="/">Go Home</Link>
            </button>
          </div>
        }
      </div>
    );
  }
};

export default Recipe;
